import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

export const Title = styled.p`
  font-size: 20px;
  margin: 5vw 5vw 2vw 5vw;
  font-weight: 550;
`;

export const SubTitle = styled.p`
  font-size: 16px;
  font-weight: 550;
`;

export const SelectedAddressContainer = styled.div`
  width: 50vw;
  background: #eeeeee;
  padding: 3vw;
  margin: 1vw 6vw;
`;

export const AddressRow = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 3vw;
  justify-content: space-between;
`;

export const Address = styled.p`
  font-size: 14px;
  width: 35vw;
`;

export const LocationIconContainer = styled.div`
  background: white;
  border-radius: 600px;
  padding: 30px;
  align-items: center;
  justify-content: center;
  display: flex;
  margin-right: 2vw;
`;

export const RadioBoxContainer = styled.button`
  border-radius: 600px;
  border: 3px solid black;
  align-items: center;
  justify-content: center;
  display: flex;
  width: 35px;
  height: 35px;
`;

export const RadioBoxSelected = styled.div`
  border-radius: 600px;
  background: var(--color-blue);
  width: 15px;
  height: 15px;
`;

export const EditarButtonContainer = styled.button`
  width: 50vw;
  margin-top: 2vw;
  border-top: 1px solid var(--color-border);
`;

export const EditarButtonLabel = styled.p`
  font-size: 14px;
  color: var(--color-blue);
`;
