import styled from "styled-components";

export const Container = styled.div`
  background: #eeeeee;
  display: flex;
  flex-direction: column;
`;

export const Title = styled.p`
  font-size: 20px;
  margin: 5vw 5vw 2vw 5vw;
  font-weight: 550;
`;

export const SubTitle = styled.p`
  font-size: 16px;
  font-weight: 550;
`;

export const SelectedAddressContainer = styled.div`
  width: 50vw;
  background: #f5f5f5;
  padding: 3vw;
  margin: 1vw 6vw;
`;

export const AddressRow = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 3vw;
`;

export const Address = styled.p`
  font-size: 14px;
`;

export const LocationIconContainer = styled.div`
  background: white;
  border-radius: 600px;
  padding: 30px;
  align-items: center;
  justify-content: center;
  display: flex;
  margin-right: 2vw;
`;

export const RadioBoxContainer = styled.div`
  border-radius: 600px;
  border-width: 5px solid black;
  align-items: center;
  justify-content: center;

  width: 150px;
  height: 150px;
`;

export const RadioBoxSelected = styled.div`
  border-radius: 600px;
  background: blue;

  width: 25px;
  height: 25px;
`;
