import React, { useEffect, useRef } from "react";
import { LiveMessage, LiveAnnouncer } from "react-aria-live";

import {
  HeaderDiv,
  Container,
  Div,
  Logo,
  SearchContainer,
  Input,
  IconContainer,
  Icon,
  LeftContainer,
  EnderecoContainer,
  Label,
  Value,
  Column,
  RightContainer,
  TransparentButton,
} from "./styles";

const Header: React.FC = () => {
  const addressRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    addressRef.current?.focus();
  }, []);

  const focusOn = () => {
    addressRef.current?.focus();
  };

  return (
    <LiveAnnouncer>
      <Container>
        <HeaderDiv>
          <LeftContainer>
            <Div className="logo">
              <Logo src={logo} />
            </Div>
            <EnderecoContainer
              ref={addressRef}
              onFocus={() => {
                alert("aa");
              }}
            >
              <Icon width={30} src={location} />
              <Column>
                <Label>Enviar para</Label>
                <Value>Ipatinga 35160208</Value>
              </Column>
            </EnderecoContainer>
          </LeftContainer>

          <SearchContainer>
            <Input
              onBlur={() => focusOn()}
              placeholder="Buscar produtos, marcas e muito mais..."
            />
            <IconContainer>
              <Icon width={25} src={search} />
            </IconContainer>
          </SearchContainer>
          {/* <DivButton className="signIn">
          <Button solid>Entrar</Button>
          <Button>Registrar-se</Button>
        </DivButton> */}
          <RightContainer>
            <TransparentButton>
              <Value>Crie a sua conta</Value>
            </TransparentButton>
            <TransparentButton>
              <Value>Entre</Value>
            </TransparentButton>
            <TransparentButton>
              <Value>Compras</Value>
            </TransparentButton>
            <TransparentButton>
              <LiveMessage
                message="Carrinho"
                aria-live="polite"
                clearOnUnmount="true"
              />
              <Icon width={25} src={cart} />
            </TransparentButton>
          </RightContainer>
        </HeaderDiv>
      </Container>
    </LiveAnnouncer>
  );
};

export default Header;

const logo = require("../../assets/logo1.png");
const search = require("../../assets/search.png");
const location = require("../../assets/location.png");
const cart = require("../../assets/cart.png");
